import React, { useState, useEffect } from 'react';
import URLShortener from './URLShortener';

const ShortenedURL = () => {
  const [originalURL, setOriginalURL] = useState('');
  const [timerSeconds, setTimerSeconds] = useState(15);
  const [showLinkButton, setShowLinkButton] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      if (timerSeconds > 0) {
        setTimerSeconds(timerSeconds - 1);
      } else {
        setShowLinkButton(true);
        clearInterval(timer);
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [timerSeconds]);

  const handleShorten = (shortenedURL) => {
    localStorage.setItem('shortenedURL', shortenedURL);
    setOriginalURL(shortenedURL);
  };

  const handleLinkClick = () => {
    const storedShortenedURL = localStorage.getItem('shortenedURL');
    
    // Perform your redirection logic based on the stored URL
    console.log('Stored Shortened Link:', storedShortenedURL);
  };

  return (
    <div className="shortened-url-container">
      <URLShortener onShorten={handleShorten} />

      <div className="ad-container">
        {/* Display ads here */}
      </div>

      {timerSeconds > 0 && (
        <div className="timer-container">
          <p>You have {timerSeconds} seconds to view the ads.</p>
        </div>
      )}

      {showLinkButton && (
        <div className="link-button-container">
          <label>Stored Movie Link:</label>
          <p>{originalURL}</p>
          <button onClick={handleLinkClick}>Get Link</button>
        </div>
      )}
    </div>
  );
};

export default ShortenedURL;
