import React, { useState, useEffect } from 'react';

const AdRedirectApp = () => {
  const [showAds, setShowAds] = useState(true);
  const [showGetLinkButton, setShowGetLinkButton] = useState(false);
  // const [redirectLink, setRedirectLink] = useState('');

  const fetchShortCodes = async () => {
    // Replace 'path/to/data.json' with the actual path to your JSON file
    const response = await fetch('./url.json');
    const data = await response.json();
    return data;
  };
  fetchShortCodes();
  useEffect(() => {
    const timer = setTimeout(() => {
      setShowGetLinkButton(true);
    }, 5000);

    // Clean up the timer to avoid memory leaks
    return () => clearTimeout(timer);
  }, []);

  const handleGetLinkClick = () => {
    // Replace 'YOUR_REDIRECT_LINK' with the actual link you want to redirect to
     const redirectLink = 'https://www.youtube.com/';
  // You can also perform any additional actions before redirecting if needed
      window.open(redirectLink, '_blank');
  };

  return (
    <div className="App">
      {showAds && (
        <div className="ads-container">
          {/* Display Google Ads here */}
          <p>Google Ads Go Here</p>
        </div>
      )}

      {showGetLinkButton && (
        <div className="get-link-container">
          <button onClick={handleGetLinkClick}>Get Link</button>
        </div>
      )}

      {/* You can add additional content or styling based on your needs */}
    </div>
  );
};

export default AdRedirectApp;
