import React, { useState } from 'react';

const URLShortener = ({ onShorten }) => {
  const [originalURL, setOriginalURL] = useState('');

  const shortenURL = (originalURL) => {
    const shortKey = Math.random().toString(36).substring(7);
    localStorage.setItem(shortKey, originalURL);
    return shortKey;
  };
  
  // Utility function to simulate URL unshortening
  const unshortenURL = (shortKey) => {
    return localStorage.getItem(shortKey);
  };

  const handleShorten = () => {
    // Simulating URL shortening
    const shortenedURL = shortenURL(originalURL);
    onShorten(shortenedURL);
  };

  return (
    <div className="url-shortener-container">
      <label>Paste the URL to shorten:</label>
      <input type="text" value={originalURL} onChange={(event) => setOriginalURL(event.target.value)} />
      <button onClick={handleShorten}>Shorten URL</button>
    </div>
  );
};

export default URLShortener;
