import React, { useEffect, useState } from 'react';

const RedirectPage = () => {
  const [showGetLinkButton, setShowGetLinkButton] = useState(false);
  const [redirectToSource, setRedirectToSource] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Attempt to retrieve data from local storage
        const storedData = localStorage.getItem('urlData');
        console.log('Stored Data:', storedData);

        // If data exists in local storage, parse and set it
        if (storedData) {
          const jsonData = JSON.parse(storedData);
          console.log('Parsed Data:', jsonData);
          setData(jsonData);
        } else {
          // Simulate fetching data from an external source
          const response = await fetch("./url.json");

          if (!response.ok) {
            throw new Error(`Network response was not ok: ${response.status}`);
          }

          const contentType = response.headers.get('content-type');

          if (contentType && contentType.includes('application/json')) {
            const jsonData = await response.json();

            // Store the fetched data in local storage
            localStorage.setItem('urlData', JSON.stringify(jsonData));

            // Set the data state
            setData(jsonData);
          } else {
            throw new Error('Response is not JSON');
          }
        }
      } catch (error) {
        console.error('Error fetching data:', error.message);
        // Handle the error, e.g., show an error message to the user
      }
    };

    fetchData();
  }, []);

  const handleGetLinkClick = () => {
    // Set the state to trigger the redirection to the source
    setRedirectToSource(true);
  };

  // Redirect to the source URL if the state is set
  if (redirectToSource) {
    window.location.href = 'https://www.example.com/original-url'; // Replace with your actual source URL
    return null; // This line is necessary to prevent rendering additional content after the redirection
  }

  return (
    <div>
      {/* "Get Link" Button */}
      {showGetLinkButton && (
        <button id="getLinkButton" onClick={handleGetLinkClick}>
          Get Link
        </button>
      )}
    </div>
  );
};

export default RedirectPage;
