import React, { useState } from 'react';

const Shortner = () => {
  const [newLink, setNewLink] = useState('');

  const handleAddLink = async () => {
    try {
      // Send a POST request to the server to add the new link
      const response = await fetch('/addLink', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url: newLink }),
      });

      if (response.ok) {
        console.log('Link added successfully');
        // You can optionally update the state or perform other actions on success
      } else {
        console.error('Failed to add link');
        // Handle error
      }
    } catch (error) {
      console.error('Error adding link:', error);
      // Handle error
    }
  };

  return (
    <div>
      <input
        type="text"
        value={newLink}
        onChange={(e) => setNewLink(e.target.value)}
        placeholder="Enter new link"
      />
      <button onClick={handleAddLink}>Add Link</button>
    </div>
  );
};

export default Shortner;
