import React, { useState } from 'react';

const Shortner = () => {
  const [links, setLinks] = useState([
    {
      id: 1,
      url: 'https://www.youtube.com/watch?v=xMd4Q8hzldM',
    },
    {
      id: 2,
      url: 'https://www.youtube.com/watch?v=nUlxZ4TzOFI',
    },
  ]);
  const [newLink, setNewLink] = useState('');

  const handleAddLink = () => {
    if (newLink.trim() === '') {
      // Don't add empty links
      return;
    }

    // Generate a new ID for the new link
    const newId = links.length + 1;

    // Add the new link to the existing array
    const newURL = { id: newId, url: newLink };
    setLinks([...links, newURL]);

    // Optionally clear the input field
    setNewLink('');
  };

  return (
    <div>
      <input
        type="text"
        value={newLink}
        onChange={(e) => setNewLink(e.target.value)}
        placeholder="Enter new link"
      />
      <button onClick={handleAddLink}>Add Link</button>

      <ul>
        {links.map((link) => (
          <li key={link.id}>
            <a href={link.url} target="_blank" rel="noopener noreferrer">
              {link.url}
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Shortner;
