import React, { useState, useEffect } from 'react';

const RedirectPage = () => {
  const [showGetLinkButton, setShowGetLinkButton] = useState(false);

  useEffect(() => {
    // Simulating fetching data from a JSON file
    const fetchData = async () => {
      // Replace 'your_data.json' with the actual path to your JSON file
      const response = await fetch('your_data.json');
      const data = await response.json();

      // Simulating data retrieval for a specific short URL
      const shortUrl = window.location.pathname.substr(1); // Extract short URL from the path
      const originalUrl = data[shortUrl];

      // Display Google Ads or perform other actions here
    };

    fetchData();

    // Set a timeout to show the "Get Link" button after 15 seconds
    const timer = setTimeout(() => {
      setShowGetLinkButton(true);
    }, 5000);

    // Cleanup the timer on component unmount
    return () => clearTimeout(timer);
  }, []);

  // Redirect to the original source URL when "Get Link" button is clicked
  const redirectToSource = () => {
    // Simulating fetching data from a JSON file
    fetch('your_data.json')
      .then(response => response.json())
      .then(data => {
        // Simulating data retrieval for a specific short URL
        const shortUrl = window.location.pathname.substr(1); // Extract short URL from the path
        const originalUrl = data[shortUrl];

        // Redirect to the original source URL
        window.location.href = originalUrl;
      });
  };

  return (
    <div>
      {/* Your Google Ads code goes here */}

      {/* Countdown Timer */}
      <div>Countdown: 15 seconds</div>

      {/* "Get Link" Button */}
      {showGetLinkButton && <button id="getLinkButton" onClick={redirectToSource}>Get Link</button>}
    </div>
  );
};

export default RedirectPage;
