import React, { useState, useEffect } from 'react';

const Shortner = () => {
  const [links, setLinks] = useState([]);
  const [newLink, setNewLink] = useState('');

  useEffect(() => {
    fetch(`http://localhost:3001/URL`)
      .then(response => response.json())
      .then(data => setLinks(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const handleAddLink = async () => {
    if (newLink.trim() === '') {
      // Don't add empty links
      return;
    }

    try {
      const response = await fetch(`./urllist.json`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url: newLink }),
      });

      if (response.ok) {
        console.log('Link added successfully');

        // Fetch the updated data after adding a new link
        const updatedData = await fetch(`http://localhost:3001/urllist/URL`).then(response => response.json());
        setLinks(updatedData);

        setNewLink('');
      } else {
        console.error('Failed to add link');
      }
    } catch (error) {
      console.error('Error adding link:', error);
    }
  };

  return (
    <div>
      <input
        type="text"
        value={newLink}
        onChange={(e) => setNewLink(e.target.value)}
        placeholder="Enter new link"
      />
      <button onClick={handleAddLink}>Add Link</button>
  
      {Array.isArray(links) ? (
        <ul>
          {links.map((link) => (
            <li key={link.id}>
              <a href={link.url} target="_blank" rel="noopener noreferrer">
                {link.url}
              </a>
            </li>
          ))}
        </ul>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Shortner;
